import React from 'react';
import './Temporizador.css';

class Temporizador extends React.Component {
  constructor(props) {
    super(props); // Invoca al constructor base
    this.state = {dateinicio: new Date(),cronometro:new Date() };
  }

  componentDidMount() {
    this.timerID = setInterval(
      () => this.iteración(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  iteración() {
    const fechaactual = new Date(); 
const diffTime = Math.abs(fechaactual - this.state.dateinicio);
    this.setState({
      date: new Date(diffTime)
    });
  }

  render() {
    return (
      <div> 
        <h2>Temporizador {this.state.date.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}
    
export default Temporizador;
