import React from 'react';
import './MenuLateral.css';

class MenuLateral extends React.Component {
  constructor(props) {
    super(props); // Invoca al constructor base
    this.state = {CantidadEvaluaciones: {Pendientes:0,Terminadas:0},Total};
  }

  
  componentDidMount() {
    const Total =    this.state.dateinicio.getMinutes()+2 -nuevafecha.getMinutes() 
    this.setState({
      CantidadEvaluaciones:  {Pendientes:15,Terminadas:20}
    });

    this.setState({
      Total:   this.state.CantidadEvaluaciones.Pendientes+2
    });
  }

  componentWillUnmount() {
 
  }

  render() {
    return (
      <div> 
          <section id="main">
          <div class="container">
            <div class="row">
              <div class="col-md-3">
              <div class="list-group">
              <a href="index.html" class="list-group-item active main-color-bg">
                <span class="glyphicon glyphicon-cog" aria-hidden="true"></span> Status
              </a>
              <a href="pages.html" class="list-group-item">
                <span class="glyphicon glyphicon-list-alt" aria-hidden="true"></span> Evaluaciones
                <span class="badge">{this.state.CantidadEvaluaciones.Total} </span>
              </a>
              <a href="posts.html" class="list-group-item">
                <span class="glyphicon glyphicon-pencil" aria-hidden="true"></span> Pendientes 
                <span class="badge"></span></a>
              <a href="users.html" class="list-group-item"><span class="glyphicon glyphicon-user" aria-hidden="true"></span> Users <span class="badge">59</span></a>
            </div>
            </div>
            </div>
            </div>
            </section> 
      </div>
    );
  }
}
     
 
export default MenuLateral;
