import React from 'react';
import './Temporizador.css';

class Temporizador extends React.Component {
  constructor(props) {
    super(props); // Invoca al constructor base
    this.state = {dateinicio: new Date(),cronometro: 0 };
  }

  componentDidMount() {
    this.timerID = setInterval(
      () => this.iteración(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  iteración() {
     let nuevafecha = new Date(); 
const diffTime =  nuevafecha - this.state.dateinicio
    this.setState({
      cronometro: new Date(diffTime)
    });
  }

  render() {
    return (
      <div> 
        <h2>Temporizador {this.state.cronometro.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}
    
export default Temporizador;
