import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";
import './EnrutamientoAnidado.css';

export default function EnrutamientoAnidado() {
  return (
    <Router>
      <div>
      <div class="col-md-12">
              <div class="list-group"> 
              <Link  className="list-group-item active main-color-bg" to="/">Inicio</Link> 
              </div> 
        <div class="list-group"> 
            <Link   className="list-group-item" to="/">Inicio</Link>
            </div>
          <div class="list-group"> 
            <Link className="list-group-item"  to="/Perfil">Perfil</Link>
            </div>
          <div class="list-group"> 
            <Link className="list-group-item"  to="/Pendientes">Pendientes</Link>
          </div> 
          </div> 
        <Switch>
          <Route path="/Perfil">
            <QuienesSomos />
          </Route>
          <Route path="/Pendientes">
            <Productos />
          </Route>
          <Route path="/">
            <Inicio />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

function Inicio() {
   return <div><h2>Inicio</h2><p>Página de Inicio</p></div>;
}
function QuienesSomos() {
   return <div><h2>Quienes Somos</h2><p>Página de Presentación</p></div>;
}

function Productos() {
  let match = useRouteMatch();

  return (
    <div>
      <h2>Productos</h2>
      <p>Página con Información de las Categorías de Productos</p>
      <ul>
        <li>
          <Link to={`${match.url}/tecnologia/celulares`}>Tecnología</Link>
        </li>
        <li>
          <Link to={`${match.url}/hogar/muebles`}>
            Hogar
          </Link>
        </li>
        <li>
          <Link to={`${match.url}/deportes/natacion`}>
            Deportes
          </Link>
        </li>
      </ul>
      <Switch>
        <Route path={`${match.path}/:categoriaId/:subcategoriaId`}>
          <Categoria />
        </Route>
        <Route path={match.path}>
          <h3>Por favor seleccione una categoría de productos.</h3>
        </Route>
      </Switch>
    </div>
  );
}

function Categoria() {  
  let categoriaId = useParams().categoriaId, subCategoríaId = useParams().subcategoriaId;
return <h3>Categoría Seleccionada: {categoriaId}, SubCategoría: {subCategoríaId}</h3>;
}
