import React from 'react';
import './Respuestas.css';

class Respuestas  extends React.Component {
  constructor(props) {
    super(props);
    this.valor=this.valor;
    this.idPregunta=this.idPregunta;
    this.Respuesta =this.Respuesta;
    this.MostrarPregunta= this.MostrarPregunta.bind(this);
    //this.state = {temperatura: ''};
  }
    
  MostrarPregunta(e) {
    //this.setState({temperatura: e.target.value});
    this.props.ejecutarCambioTemperatura(e.target.value);
  }

  render() {
      let valor = this.props.valor;
      let idPregunta = this.props.idPregunta;
      let Respuesta = this.props.Respuesta;
     return (
      <div>
      <div className="Preguntas"> 
      <input type="radio" value={valor} name={idPregunta}    onChange={this.MostrarPregunta} />  {Respuesta} 
      </div> 
    </div>
    );}
  }
  
export default Respuestas;
